const dayNight=document.querySelector(".day-night");dayNight.addEventListener("click",()=>{dayNight.querySelector("i").classList.toggle("fa-sun"),document.body.classList.toggle("dark")}),window.addEventListener("load",()=>{document.body.classList.contains("dark")?dayNight.querySelector("i").classList.add("fa-sun"):dayNight.querySelector("i").classList.add("fa-moon")});